public class Summe {

	/**
	 * @param args
	 */
	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int x = Integer.parseInt(args[0]);
		int y = Integer.parseInt(args[1]);

		int summe = x + y;
		System.out.println(x + " + " + y + " = " + summe);

	}

}
